import React, {useState, useEffect} from 'react';
import Login from './components/Login';
import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';
import TaskFilter from './components/TaskFilter';
import {getFromStorage, saveToStorage, STORAGE_KEYS} from './utils/localStorage';

function App() {
    const [user, setUser] = useState(null);
    const [tasks, setTasks] = useState([]);
    const [filter, setFilter] = useState('all');
    const [showForm, setShowForm] = useState(false);
    const [editingTask, setEditingTask] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    // Load data from localStorage on app initialization
    useEffect(() => {
        const loadData = () => {
            try {
                const savedUser = getFromStorage(STORAGE_KEYS.USER);
                const savedTasks = getFromStorage(STORAGE_KEYS.TASKS);

                if (savedUser) {
                    setUser(savedUser);
                }

                if (savedTasks && Array.isArray(savedTasks)) {
                    setTasks(savedTasks);
                }
            } catch (error) {
                console.error('Error loading data:', error);
            } finally {
                setIsLoading(false);
            }
        };

        loadData();
    }, []);

    // Save tasks to localStorage whenever tasks change
    useEffect(() => {
        if (!isLoading) {
            saveToStorage(STORAGE_KEYS.TASKS, tasks);
        }
    }, [tasks, isLoading]);

    // Handle user login
    const handleLogin = (username) => {
        const userData = {
            username,
            loginTime: new Date().toISOString()
        };
        setUser(userData);
        saveToStorage(STORAGE_KEYS.USER, userData);
    };

    // Handle user logout
    const handleLogout = () => {
        setUser(null);
        saveToStorage(STORAGE_KEYS.USER, null);
        // Optionally clear tasks on logout
        // setTasks([]);
        // saveToStorage(STORAGE_KEYS.TASKS, []);
    };

    // Add new task
    const addTask = (taskData) => {
        const newTask = {
            id: Date.now() + Math.random(), // More unique ID
            ...taskData,
            completed: false,
            createdAt: new Date().toISOString(),
            updatedAt: new Date().toISOString()
        };

        setTasks(prev => [newTask, ...prev]); // Add to beginning for recent-first order
        setShowForm(false);
    };

    // Update existing task
    const updateTask = (taskId, updatedData) => {
        setTasks(prev => prev.map(task =>
            task.id === taskId
                ? {
                    ...task,
                    ...updatedData,
                    updatedAt: new Date().toISOString()
                }
                : task
        ));
        setEditingTask(null);
    };

    // Delete task
    const deleteTask = (taskId) => {
        setTasks(prev => prev.filter(task => task.id !== taskId));
    };

    // Toggle task completion
    const toggleComplete = (taskId) => {
        setTasks(prev => prev.map(task =>
            task.id === taskId
                ? {
                    ...task,
                    completed: !task.completed,
                    updatedAt: new Date().toISOString()
                }
                : task
        ));
    };

    // Handle editing task
    const handleEditTask = (task) => {
        setEditingTask(task);
        setShowForm(false); // Close add form if open
    };

    // Handle canceling forms
    const handleCancelForm = () => {
        setShowForm(false);
        setEditingTask(null);
    };

    // Filter tasks based on current filter
    const filteredTasks = tasks.filter(task => {
        switch (filter) {
            case 'completed':
                return task.completed;
            case 'pending':
                return !task.completed;
            default:
                return true;
        }
    });

    // Calculate task counts
    const taskCounts = {
        all: tasks.length,
        completed: tasks.filter(t => t.completed).length,
        pending: tasks.filter(t => !t.completed).length
    };

    // Show loading screen
    if (isLoading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner">📋</div>
                <p>Loading your tasks...</p>
            </div>
        );
    }

    // Show login if no user
    if (!user) {
        return <Login onLogin={handleLogin}/>;
    }

    return (
        <div className="app">
            <header className="app-header">
                <div className="header-content">
                    <div className="header-left">
                        <h1>📋 Personal Task Tracker</h1>
                        <span className="app-subtitle">Stay organized, stay productive</span>
                    </div>
                    <div className="header-right">
                        <div className="user-info">
                            <span className="welcome-text">Welcome back, <strong>{user.username}</strong>!</span>
                            <button onClick={handleLogout} className="logout-btn">
                                Logout
                            </button>
                        </div>
                    </div>
                </div>
            </header>

            <main className="app-main">
                <div className="main-content">
                    <div className="task-controls">
                        <button
                            onClick={() => {
                                setShowForm(!showForm);
                                setEditingTask(null); // Close edit form if open
                            }}
                            className={`add-task-btn ${showForm ? 'active' : ''}`}
                        >
                            {showForm ? '✕ Cancel' : '+ Add New Task'}
                        </button>

                        <TaskFilter
                            currentFilter={filter}
                            onFilterChange={setFilter}
                            taskCounts={taskCounts}
                        />
                    </div>

                    {/* Add Task Form */}
                    {showForm && (
                        <TaskForm
                            onSubmit={addTask}
                            onCancel={handleCancelForm}
                        />
                    )}

                    {/* Edit Task Form */}
                    {editingTask && (
                        <TaskForm
                            task={editingTask}
                            onSubmit={(data) => updateTask(editingTask.id, data)}
                            onCancel={handleCancelForm}
                            isEditing={true}
                        />
                    )}

                    {/* Task List */}
                    <TaskList
                        tasks={filteredTasks}
                        onToggleComplete={toggleComplete}
                        onEdit={handleEditTask}
                        onDelete={deleteTask}
                        currentFilter={filter}
                    />
                </div>
            </main>

            <footer className="app-footer">
                <p>Made with ❤️ for better task management</p>
            </footer>
        </div>
    );
}

export default App;
