import React from 'react';

const TaskItem = ({task, onToggleComplete, onEdit, onDelete}) => {
    const formatDate = (dateString) => {
        try {
            const date = new Date(dateString);
            const now = new Date();
            const diffTime = Math.abs(now - date);
            const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

            // If less than 24 hours ago, show relative time
            if (diffDays <= 1) {
                const diffHours = Math.floor(diffTime / (1000 * 60 * 60));
                const diffMinutes = Math.floor(diffTime / (1000 * 60));

                if (diffHours < 1) {
                    return diffMinutes <= 1 ? 'Just now' : `${diffMinutes} minutes ago`;
                }
                return diffHours === 1 ? '1 hour ago' : `${diffHours} hours ago`;
            }

            // Otherwise show formatted date
            return date.toLocaleDateString('en-US', {
                year: 'numeric',
                month: 'short',
                day: 'numeric',
                hour: '2-digit',
                minute: '2-digit'
            });
        } catch (error) {
            return 'Invalid date';
        }
    };

    const handleDelete = () => {
        if (window.confirm(`Are you sure you want to delete "${task.title}"?`)) {
            onDelete(task.id);
        }
    };

    const handleToggleComplete = () => {
        onToggleComplete(task.id);
    };

    const handleEdit = () => {
        onEdit(task);
    };

    return (
        <div className={`task-item ${task.completed ? 'completed' : 'pending'}`}>
            <div className="task-checkbox">
                <label className="checkbox-container">
                    <input
                        type="checkbox"
                        checked={task.completed}
                        onChange={handleToggleComplete}
                        aria-label={`Mark "${task.title}" as ${task.completed ? 'pending' : 'completed'}`}
                    />
                    <span className="checkmark"></span>
                </label>
            </div>

            <div className="task-content" onClick={handleToggleComplete}>
                <div className="task-header">
                    <h4 className="task-title">{task.title}</h4>
                    <div className="task-status">
            <span className={`status-badge ${task.completed ? 'completed' : 'pending'}`}>
              {task.completed ? '‚úÖ Completed' : '‚è≥ Pending'}
            </span>
                    </div>
                </div>

                {task.description && (
                    <p className="task-description">{task.description}</p>
                )}

                <div className="task-meta">
          <span className="task-date" title={new Date(task.createdAt).toLocaleString()}>
            üìÖ Created: {formatDate(task.createdAt)}
          </span>
                </div>
            </div>

            <div className="task-actions">
                <button
                    onClick={handleEdit}
                    className="action-btn edit-btn"
                    title="Edit task"
                    aria-label={`Edit "${task.title}"`}
                >
                    <span className="btn-icon">‚úèÔ∏è</span>
                    <span className="btn-text">Edit</span>
                </button>
                <button
                    onClick={handleDelete}
                    className="action-btn delete-btn"
                    title="Delete task"
                    aria-label={`Delete "${task.title}"`}
                >
                    <span className="btn-icon">üóëÔ∏è</span>
                    <span className="btn-text">Delete</span>
                </button>
            </div>
        </div>
    );
};

export default TaskItem;
