import React from 'react';

const TaskFilter = ({currentFilter, onFilterChange, taskCounts}) => {
    const filters = [
        {
            key: 'all',
            label: 'All Tasks',
            count: taskCounts.all,
            icon: 'üìã'
        },
        {
            key: 'pending',
            label: 'Pending',
            count: taskCounts.pending,
            icon: '‚è≥'
        },
        {
            key: 'completed',
            label: 'Completed',
            count: taskCounts.completed,
            icon: '‚úÖ'
        }
    ];

    return (
        <div className="task-filter">
            <div className="filter-tabs">
                {filters.map(filter => (
                    <button
                        key={filter.key}
                        onClick={() => onFilterChange(filter.key)}
                        className={`filter-tab ${currentFilter === filter.key ? 'active' : ''}`}
                        aria-pressed={currentFilter === filter.key}
                    >
                        <span className="filter-icon">{filter.icon}</span>
                        <span className="filter-label">{filter.label}</span>
                        <span className="filter-count">{filter.count}</span>
                    </button>
                ))}
            </div>

            <div className="filter-summary">
        <span className="summary-text">
          Showing {taskCounts[currentFilter]} {currentFilter === 'all' ? 'total' : currentFilter}
            {taskCounts[currentFilter] === 1 ? ' task' : ' tasks'}
        </span>
            </div>
        </div>
    );
};

export default TaskFilter;
